<template>
  <div class="customerService">
    <div class="headImageview">
      <img class="headImage" :src="avatarUrl" />
    </div>
    <div class="nameview">
      <span class="name">{{nickName}}</span>
    </div>
    <!-- <div class="buttview">
      <div
        :class="'nav-son '+(shownavindex == 1? 'active' : '')"
        @click="supplierTap"
        data-nav="1"
      >
        <div class="content">消費者</div>
      </div>
      <div
        :class="'nav-son '+(shownavindex == 2? 'active' : '')"
        @click="consumerTap"
        data-nav="2"
      >
        <div class="content">供應商</div>
      </div>
    </div>-->
    <textarea
      class="textareacontent"
      @focus="focusa"
      maxlength="160"
      :placeholder="holder"
      @input="blurs"
      @blur="blurs"
      :style="'span-indent:'+fours"
      v-model="vals"
    ></textarea>
    <div class="tabbarview">
      <div class="tabbar-left" @click="messageTap">
        <div v-if="messageNum" class="numberView">{{messageNum}}</div>消息
        <i class="iconfont icon-xiaoxi"></i>
      </div>
      <div class="tabbar-right" @click="submitTap">
        <div class="tabbar-rightview">立即提交</div>
      </div>
    </div>
  </div>
</template>
<script>
export default {
  data() {
    return {
      shownavindex: "1",
      current: "1",
      opinion: "",
      name: "哈哈",
      fours: "50rpx",
      holder: "請把您的問題與建議寫下提交，我們會盡快回復您，謝謝!",
      nickName: "名字",
      avatarUrl: "../../image/morentouxiang.png",
      messageNum: "",
      vals: ""
    };
  },
  /**
   * 生命周期函数--监听页面加载
   */
  onLoad: function(options) {
    this.nickName = mpvue.getStorageSync("nickName");
    this.avatarUrl = mpvue.getStorageSync("avatarUrl");
  },
  /**
   * 生命周期函数--监听页面显示
   */
  onShow: function() {
    var _this = this;
    //判断是否登录(登录态是否过期)
    var value = mpvue.getStorageSync("sessionId");
    if (value) {
      //登录过
      //判断登录态是否过期
      mpvue.request({
        url: this.config.service.timeout,
        data: {
          args: {
            sessionId: value
          }
        },
        success: function(res) {
          console.log("success", res.data.Code);
          if (res.data.Code == 200) {
            //使用储存的信息
            _this.nickName = mpvue.getStorageSync("nickName");
            _this.avatarUrl = mpvue.getStorageSync("avatarUrl");
          } else {
            //登录态过时删除保存的登录态
            mpvue.removeStorageSync("sessionId");
          }
        },
        fail: function(res) {
          console.log("fail", res.data);
        }
      });
    }

    mpvue.request({
      url: this.config.service.searchUnreadMessage,
      data: {
        args: {
          userId: mpvue.getStorageSync("userId"),
          sessionId: mpvue.getStorageSync("sessionId")
        }
      },
      success: function(res) {
        if (res.data.Code) {
          if (res.data.Code == 200) {
            _this.messageNum = res.data.Data;
            console.log("success", res.data.Data);
          } else {
            console.log("fail", res.data);
          }
        } else {
          console.log("fail", res.data);
        }
      },
      fail: function(res) {
        mpvue.showToast({
          title: "網絡異常",
          icon: "none",
          duration: 2000
        });
        console.log("fail", res.data);
      }
    });
  },
  methods: {
    supplierTap: function(e) {
      this.shownavindex = e.mp.currentTarget.dataset.nav;
      this.current = "1";
      console.log("点击消费者：", e.mp.currentTarget.dataset.nav);
    },

    consumerTap: function(e) {
      this.shownavindex = e.mp.currentTarget.dataset.nav;
      this.current = "2";
      console.log("点击供应商：", e.mp.currentTarget.dataset.nav);
    },

    messageTap: function(e) {
      //跳转消息
      mpvue.navigateTo({ url: "../myMessage/main?title=message" });
    },

    submitTap: function(e) {
      //提交问题

      var that = this;
      if (this.current == 1) {
        //
        console.log("点击消费者 意见:" + this.opinion);
      } else if (this.current == 2) {
        console.log("点击供应商 意见:" + this.opinion);
      }

      if (this.opinion == "") {
        mpvue.showToast({
          title: "請填寫問題和建議",
          icon: "none",
          duration: 2000
        });
      } else {
        mpvue.showLoading({
          title: "請稍後"
        });
        mpvue.request({
          url: `${this.config.service.suggest}`,
          data: {
            args: {
              wxuserId: mpvue.getStorageSync("userId"),
              sessionId: mpvue.getStorageSync("sessionId"),
              userType: "1",
              problem: this.opinion
            }
          },
          success: function(res) {
            mpvue.hideLoading();
            if (res.data.Code) {
              if (res.data.Code == 200) {
                mpvue.showToast({
                  title: "提交成功",
                  icon: "success",
                  duration: 2000
                });
                that.opinion = "";
                that.holder =
                  "請把您的問題與建議寫下提交，我們會盡快回復您，謝謝!";
                that.vals = "";
                console.log("success", res.data.Data);
              } else {
                mpvue.showToast({
                  title: "提交失敗",
                  icon: "none",
                  duration: 2000
                });
                console.log("fail", res.data);
              }
            } else {
              mpvue.showToast({
                title: "提交失敗",
                icon: "none",
                duration: 2000
              });
              console.log("fail", res.data);
            }
          },
          fail: function(res) {
            mpvue.hideLoading();
            mpvue.showToast({
              title: "提交失敗",
              icon: "none",
              duration: 2000
            });
            console.log("fail", res.data);
          }
        });
      }
    },

    blurs: function(e) {
      //点击完成保存数据
      var vue = e.mp.detail.value;
      this.name = "" + vue;
      this.opinion = "" + vue;

      if (vue == "") {
        this.fours = "50rpx";
        this.holder = "請把您的問題與建議寫下提交，我們會盡快回復您，謝謝!";
      }
    },
    focusa: function() {
      this.fours = "0rpx";
      this.holder = "";
    }
  },
  mounted() {
    mpvue.setNavigationBarColor({
      frontColor: "#ffffff",
      backgroundColor: "#00A980",
      animation: {
        duration: 400,
        timingFunc: "easeIn"
      }
    });
  }
};
</script>
